css:
.copilot {
  min-height: 100vh;
  background-color: #e0f7fa;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  font-family: sans-serif;
  color: #006064;
  padding: 2rem;
}
.copilot button {
  background-color: #00796b;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 5px;
  cursor: pointer;
  font-size: 1rem;
}
.copilot button:hover {
  background-color: #004d40;
}
