css:
.toolbar {
  display: flex;
  justify-content: center;
  gap: 1rem;
  background-color: #333;
  padding: 1rem;
}
.toolbar button {
  background-color: #ff6600;
  color: white;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 5px;
  cursor: pointer;
}
.toolbar button:hover {
  background-color: #ff8800;
}
